    .placeholder();
}

// Helpers
.clickable(@opacity) {
    cursor: pointer;
    opacity: @opacity;

    &:hover {
        opacity: 1;
    }
}

.disabled() {
    background-color: @bg-color !important;
}

.focused() {
    &:not([readonly="readonly"]):not([disabled]) {
        border-color: @color-primary !important;
    }
    outline: 0;
    box-shadow: none; 
}

.rotate(@radius) {
    /* Rotate div */
    -ms-transform: rotate(@radius); /* IE 9 */
    -webkit-transform: rotate(@radius); /* Chrome, Safari, Opera */
    transform: rotate(@radius);
}

